const state = {
    secret: dictionary[Math.floor(Math.random() * dictionary.length)],
    grid: Array(6).fill().map(() => Array(5).fill()), 
    currentRow: 0, 
    currentCol: 0,
};


function updateGrid() {
    for(let i=0; i<state.grid.length; i++) {
        for(let j=0; j<state.grid[i].length; j++) {
            const box = document.getElementById(`box${i}${j}`);
            box.textContent = state.grid[i][j];
        }
    }
}


function drawBox(container, row, col, letter = '') {
    const box = document.createElement('div');
    box.className = 'box';
    box.id = `box${row}${col}`;
    box.textContent = letter;
    container.appendChild(box);
    return box;
}


function drawGrid(container) {
    const grid = document.createElement('div');
    grid.className = 'grid';
    for(let i=0; i<6; i++) {
        for(let j=0; j<5; j++) {
            drawBox(grid, i, j);
        }
    }
    container.appendChild(grid);
}


function isLetter(key) {
    return key.length === 1 && key.toLowerCase() !== key.toUpperCase();
}


function addLetter(letter) {
    if(state.currentCol === 5) {
        return;
    }
    state.grid[state.currentRow][state.currentCol] = letter;
    state.currentCol++;
}


function removeLastLetter() {
    if(state.currentCol === 0) {
        return;
    }
    state.grid[state.currentRow][state.currentCol - 1] = '';
    state.currentCol--;
}


function getCurrentWord(word) {
    return state.grid[state.currentRow].reduce((prev, curr) => prev + curr);
}


function isValidWord(word) {
    return dictionary.includes(word);
}


function revealWord(guess) {
    const row = state.currentRow;
    const animationDuration = 500;

    for(let i=0; i<5; i++) {
        const box = document.getElementById(`box${row}${i}`);
        const letter = box.textContent;

        setTimeout(() => {
            if(letter === state.secret[i]) {
                box.classList.add('right');
            }
            else if(state.secret.includes(letter)) {
                box.classList.add('wrong');
            }
            else {
                box.classList.add('empty');
            }
        }, ((i + 1) * animationDuration) / 2);

        box.classList.add('animated');
        box.style.animationDelay = `${(i * animationDuration) / 2}ms`;

    }
    
    const isWinner = state.secret === guess;
    const gameOver = state.currentRow === 5;

    setTimeout(() => {
        if(isWinner) {
            alert("CONGRATS!");        
        }
        else if(gameOver) {
            alert(`Game over. The correct word was ${state.secret.toUpperCase()}.`);
        }        
    }, 3 * animationDuration);
}


function recordInputs() {
    document.body.onkeydown = (e) => {
        const key = e.key;
        if(key === 'Enter') {
            if(state.currentCol === 5) {
                const word = getCurrentWord();
                if(isValidWord(word)) {
                    revealWord(word);
                    state.currentRow++;
                    state.currentCol = 0;
                }
                else {
                    alert("Not in word list!");
                }
            }
        }
        if(key === 'Backspace') {
            removeLastLetter();
        }
        if(isLetter(key)) {
            addLetter(key);
        }
        updateGrid();
    };
}


function startGame() {
    const game = document.getElementById('game');
    drawGrid(game);
    recordInputs();
    console.log(`Word is ${state.secret.toUpperCase()}`);
}

startGame();